function getTrips(){$.getJSON(TRIPS_URL,{format:"json"}).done(function(a){a.map(function(a){renderTripList(a)});getAndDisplayTrip(a[0])})}function renderTripHeader(a){var s='  \n    <div class="trip-header__photo-container">\n      <img src="'+a.media[0].file+'" alt="'+a.media[0].caption+'" class="trip-header__photo">\n    </div>\n    <article class="trip-header__info">\n      <h2 class="heading__x-large">'+a.title+'</h2>\n      <div class="user-profile">\n        <img src="http://placebeyonce.com/80-80" class="user-profile__photo" alt="">\n        <p class="user-profile__name">\n          By Brenda Storer, New York City\n        </p>\n      </div>\n      <p class="trip__description">'+a.description+'</p>\n      <ul class="trips-list__interests">\n        '+a.interests.map(function(a){return'<li class="tag">'+a+"</li>"}).join(" ")+"\n      </ul>\n    </article>\n  ";$(".js-trip-header").prepend(s)}function renderCalendarDays(a){var s=moment(a.calendarDate).format("dddd"),n=moment(a.calendarDate).format("MMM D"),i='\n    <a href="#" class="trip-calendar__day" data-day="'+a.calendarDate+'">\n      <span class="heading__small-caps">\n        '+s+'\n      </span>\n      <span class="trip-calendar__date">\n        '+n+"\n      </span>\n    </a>\n  ";$(".js-trip-calendar").append(i)}function renderTripDay(a){var s='\n    <aside class="trip-day__sidebar">\n      <section class="trip-day__sidebar-section">\n        <h3 class="heading__small-caps">Places</h3>\n        <h4 class="heading__medium">'+a.days[0].locations[0].name+", "+a.days[0].locations[0].country+'</h4>\n        <a href="#" class="modal__link">\n          '+a.days[0].locations[0].sites[0].name+'\n        </a>\n        <a href="#" class="modal__link">\n          '+a.days[0].locations[0].sites[1].name+'\n        </a>\n      </section>\n      <section class="trip-day__sidebar-section">\n        <h3 class="heading__small-caps">Lodging</h3>\n        <a href="#" class="modal__link">\n          '+a.days[0].locations[0].sites[2].name+"<br>\n          "+a.days[0].locations[0].sites[2].address+'\n        </a>\n      </section>\n      <section class="trip-day__sidebar-section">\n        <h3 class="heading__small-caps">Travel Details</h3>\n        '+a.days[0].travelDetails+'\n      </section>\n    </aside>\n    <article class="trip-day__diary">\n      '+a.days[0].diaryEntry+'\n      <div class="trip-slideshow js-trip-slideshow"></div>\n    </article>\n  ';$(".js-trip-day").prepend(s)}function renderTripPhotos(a){var s='\n    <img src="'+a.file+'" alt="'+a.caption+'" class="trip-slideshow__photo">\n  ';$(".js-trip-slideshow").append(s)}function displayCalendarDays(a){a.days.map(function(a){renderCalendarDays(a)})}function displayTripPhotos(a){a.media.map(function(a){renderTripPhotos(a)})}function getAndDisplayTrip(a){renderTripHeader(a),displayCalendarDays(a),renderTripDay(a),displayTripPhotos(a)}function renderTripList(a){var s=moment(a.startDate).format("LL"),n=moment(a.endDate).format("LL"),i='\n    <li class="trips-list__trip">\n      <a href="'+a.tripUrl+'" class="trips-list__link">\n        <div class="trips-list__photo-container">\n          <img src="'+a.media[0].file+'" alt="'+a.media[0].caption+'" class="trips-list__photo">\n        </div>\n        <article class="trips-list__info">\n          <header class="trips-list__header">\n            <h2 class="heading__large">'+a.title+"</h2>\n          </header>\n          <p>"+s+" &ndash; "+n+'</p>\n          <p class="trips-list__description">'+a.description+'</p>\n          <ul class="trips-list__interests">\n            '+a.interests.map(function(a){return'<li class="tag">'+a+"</li>"}).join(" ")+"\n          </ul>\n        </article>\n      </a>\n    </li>\n  ";$(".js-trips-list").append(i)}$(function(){getTrips()});var TRIPS_URL="/trips";